import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import Card from 'react-bootstrap/Card';
import React,{useRef,useState} from 'react';
import axios from 'axios';

const imagePath = '/assects/images/early blide.jpg'
let fapiURL = 'http://127.0.0.1:8000/image'
const plantName_ = 'Potato'
const diseaseName_ = 'Early Blight'
const imgTitle_ = 'Plant Disease Predction'
const confidence_ = '98.2'

const Text=({txt})=>{return (<><Card.Text>{txt}</Card.Text></>);}

export function ImageCard({api,se}) {
        const fileInputRef = useRef(null);
        const [selectedFile, setSelectedFile] = useState(null);
        const handleButtonClick = () => {
        // Trigger the file input when the button is clicked
        fileInputRef.current.click();
      };
      const handleFileChange = (event) => {
        // Handle file change here
        const file = event.target.files[0];
        setSelectedFile(file);
      };
    
  return (
    <div className='card-container'>
    <Card className='custom-card'>
      <Card.Img 
        className='card-image'
        variant="top" 
        src={selectedFile ? URL.createObjectURL(selectedFile) : imagePath}
      />
      <Card.Body>
        <Card.Title>Plant Disease Classification</Card.Title>
        <Text txt={`Plant Name : ${plantName ? {} : 'Null'}`}/>
        <Text txt={`Disease Name : ${diseaseName ? {}: 'Null'}`}/>
        <Text txt={`Confidence : ${confidence ? {}: '0.0' }%`}/>
        <Button onClick={handleButtonClick} variant="primary"
        >Upload & Predict</Button>
        <input type='file' ref={fileInputRef} onChange={handleFileChange} style={{ display: 'none' }} ></input>

      </Card.Body>
    </Card>
    </div>
  );
}

export function HandleImgCard({ api, setApi }) {
    const fileInputRef = useRef(null);
    const handleButtonClick = () => {
        // Trigger the file input when the button is clicked
        fileInputRef.current.click();
      };
      const handleFileChange = (event) => {
        // Handle file change here
        console.log('Selected file:', event.target.files[0].name);
      };
    return (
      <div>
        <ImageCard/>
        <input type='file' ref={fileInputRef} onChange={handleFileChange} style={{ display: 'none' }} ></input>
        <Button onClick={handleButtonClick}>click</Button>
      </div>
    );
  }
