import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import Card from 'react-bootstrap/Card';
import React,{useRef,useState} from 'react';
import axios from 'axios';

const imagePath = '/assects/images/early blide.jpg'
let fapiURL = 'http://127.0.0.1:8000/image'


const Text=({txt})=>{return (<><Card.Text>{txt}</Card.Text></>);}

export function ImageCard({cardTitle,plantName,
    diseaseName,confidence}) {
        const fileInputRef = useRef(null);
        const [selectedFile, setSelectedFile] = useState(null);
        const handleButtonClick = () => {
        // Trigger the file input when the button is clicked
        fileInputRef.current.click();
      };
      const handleFileChange = async (event) => {
        // Handle file change here
        const file = event.target.files[0];
        setSelectedFile(file);

        if (file) {
            try {
              // Create FormData object to send the file
              const formData = new FormData();
              formData.append('file', file);
      
              // Make a POST request to your FastAPI endpoint using Axios
              const response = await axios.post(fapiURL, formData);
      
              // Check if the response is successful
              if (response.status === 200) {
                // Parse the JSON data from the response
                const jsonData = response;
                console.log(jsonData)
              } else {
                // Handle error response
                console.error('Error:', response.status, response.statusText);
              }
            } catch (error) {
              // Handle network or other errors
              console.error('Error:', error.message);
            }
          }

      };
  return (
    <div className='card-container'>
    <Card className='custom-card'>
      <Card.Img 
        className='card-image'
        variant="top" 
        src={selectedFile ? URL.createObjectURL(selectedFile) : imagePath}
      />
      <Card.Body>
        <Card.Title>{cardTitle ? {} : 'Plant Disease Classification'}</Card.Title>
        <Text txt={`Plant Name : ${plantName ? {} : 'Null'}`}/>
        <Text txt={`Disease Name : ${diseaseName ? {}: 'Null'}`}/>
        <Text txt={`Confidence : ${confidence ? {}: '0.0' }%`}/>
        <Button onClick={handleButtonClick} variant="primary"
        >Upload & Predict</Button>
        <input type='file' ref={fileInputRef} onChange={handleFileChange} style={{ display: 'none' }} ></input>

      </Card.Body>
    </Card>
    </div>
  );
}

const apiHandle = async ({file})=>{

    try{
        const formData = new FormData();
        formData.append('file',file)
        const response = await axios.post(fapiURL, formData);
        if (response.status===200){
            const apiResponse = response;
        }
    }

    catch (error){
        console.error('Error in uploading Image',error.message);
    }
}

export function HandleApi({ api, setApi }) {
    return (
      <div>
        <ImageCard/>
      </div>
    );
  }
