import React, { useRef, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import axios from 'axios';

const fapiURL = 'http://127.0.0.1:8000/image';

const Text = ({ txt }) => {
  return (
    <Card.Text>
      {txt}
    </Card.Text>
  );
}

export function ImageCard({ cardTitle, plantName, diseaseName, confidence, imgLink }) {
  return (
    <div className='card-container'>
      <Card className='custom-card'>
        <Card.Img
          className='card-image'
          variant="top"
          src={imgLink}
        />
        <Card.Body>
          <Card.Title>{cardTitle}</Card.Title>
          <Text txt={`Plant Name : ${plantName}`} />
          <Text txt={`Disease Name : ${diseaseName}`} />
          <Text txt={`Confidence : ${confidence}%`} />
          <Button variant="primary">Predict</Button>
        </Card.Body>
      </Card>
    </div>
  );
}

export function HandleImgCard({ api, setApi }) {
  const [img, setImage] = useState(null);
  const inputRef = useRef(null);

  const handleImgClick = () => {
    inputRef.current.click();
  };

  const handleImgChange = (event) => {
    const file = event.target.files[0];
    setImage(file);
    uploadImage(file);
  };

  const uploadImage = async (file) => {
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(fapiURL, formData);

      if (response.status === 200) {
        const dimensions = response.data;
        setApi(dimensions);
      } else {
        console.error(`Failed to upload image: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      console.error('Error uploading image:', error.message);
    }
  };

  return (
    <div>
      <ImageCard
        cardTitle="Plant Disease Prediction"
        plantName="Potato"
        diseaseName="Early Blight"
        confidence="98.2"
        imgLink={img ? URL.createObjectURL(img) : '/placeholder.jpg'}
      />
      <input
        type="file"
        style={{ display: 'none' }}
        ref={inputRef}
        onChange={handleImgChange}
      />
      <Button variant="primary" onClick={handleImgClick}>
        Upload Image
      </Button>
    </div>
  );
}
