import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import Card from 'react-bootstrap/Card';

const Text=({txt})=>{
    return (
        <>
            <Card.Text>
            {txt}
            </Card.Text>
        </>
    );
}

export function ImageCard({cardTitle,cardText,imgLink}) {
  return (
    <div className='card-container'>
    <Card className='custom-card'>
      <Card.Img className='card-image' variant="top" src={imgLink} />
      <Card.Body>
        <Card.Title>{cardTitle}</Card.Title>
        <Card.Text className='card-text'>
          {cardText}
        </Card.Text>
        <Text />
        <a href='https://science.nasa.gov/astrophysics/focus-areas/black-holes/'><Button variant="primary">Show</Button></a>
      </Card.Body>
    </Card>
    </div>
  );
}
