import React, { useState, useRef } from 'react';

export function ImageCard({ imgLink }) {
  const [isAlternateImage, setIsAlternateImage] = useState(false);
  const fileInputRef = useRef(null);

  const handleImageClick = () => {
    setIsAlternateImage(!isAlternateImage);
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    console.log('Selected File:', selectedFile);
  };

  const getImageSrc = () => {
    return isAlternateImage
      ? 'https://via.placeholder.com/150/0000FF/808080?text=Alternate+Image'
      : imgLink;
  };

  return (
    <div>
      <img
        src={getImageSrc()}
        alt="Plant Image"
        onClick={handleImageClick}
        style={{ cursor: 'pointer' }}
      />

      {/* Hidden file input */}
      <input
        type="file"
        style={{ display: 'none' }}
        ref={fileInputRef}
        onChange={handleFileChange}
      />
    </div>
  );
}
