import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import Card from 'react-bootstrap/Card';
import React,{useRef,useState} from 'react';
import axios from 'axios';

const imagePath = '/assects/images/early blide.jpg'
let fapiURL = 'http://127.0.0.1:8000/image'


const Text = ({ txt }) => {
    return <><Card.Text>{txt}</Card.Text></>;
  };
 
  export function ImageCard({ cardTitle, plantName, diseaseName, confidence }) {
    const fileInputRef = useRef(null);
    const [selectedFile, setSelectedFile] = useState(null);
    
  
    const handleButtonClick = () => {
      // Trigger the file input when the button is clicked
      fileInputRef.current.click();
    };
  
    const handleFileChange = (event) => {
      // Handle file change here
      const file = event.target.files[0];
      setSelectedFile(file);
  
      if (file) {
        apiHandle(file, fapiURL)
          .then((response) => {
            // Use the response data here
            if (response) {
              const { plantName, diseaseName, confidence } = response.data;
              // Update the state or perform any other actions with the response data
            }
          })
          .catch((error) => {
            console.error('Error in API call:', error);
          });
      }
    };
  
    return (
      <div className='card-container'>
        <Card className='custom-card'>
          <Card.Img
            className='card-image'
            variant="top"
            src={selectedFile ? URL.createObjectURL(selectedFile) : imagePath}
          />
          <Card.Body>
            <Card.Title>{cardTitle ? {} : 'Plant Disease Classification'}</Card.Title>
            <Text txt={`Plant Name : ${plantName ? {} : 'Null'}`} />
            <Text txt={`Disease Name : ${diseaseName ? {} : 'Null'}`} />
            <Text txt={`Confidence : ${confidence ? {} : '0.0'}%`} />
            <Button onClick={handleButtonClick} variant="primary">Upload & Predict</Button>
            <input type='file' ref={fileInputRef} onChange={handleFileChange} style={{ display: 'none' }} />
          </Card.Body>
        </Card>
      </div>
    );
  }
  
  const apiHandle = async ({ file, apiURL }) => {
    try {
      const formData = new FormData();
      formData.append('file', file);
  
      // Make a POST request to the specified API URL
      const response = await axios.post(apiURL, formData);
  
      // Check if the response status is 200 (OK)
      if (response.status === 200) {
        // Return the entire response object
        return response;
      } else {
        // If the response status is not 200, handle the error
        console.error('Error in uploading Image:', response.status, response.statusText);
        // Throw an error to be caught in the catch block
        throw new Error('API request failed');
      }
    } catch (error) {
      // Handle network or other errors
      console.error('Error in uploading Image:', error.message);
      // Throw an error to be caught in the catch block
      throw new Error('API request failed');
    }
  };
  

export function HandleApi({ api, setApi }) {
    return (
      <div>
        <ImageCard/>
      </div>
    );
  }
